import Layout from "../components/catbyproduct/Layout";
import { withRouter } from 'next/router';
import client from "../components/ApolloClient";
import PRODUCTS from "../queries/product";
import Productlistbycat from "../components/catbyproduct/Productlistbycat";
import Sinpro from "../components/product/Sinpro";

const singleproduct = withRouter( props => {

	const { categoryName, products } = props;

	return (
		<Layout>
			<div className="content-wrap">
				{ categoryName ? <h3 className="product-container pl-5">{ categoryName }</h3> : '' }
				<div className="product-container row">
					{ undefined !== products && products.length ? (
						products.map( lst => <Sinpro key={ lst.id } lst={ lst } /> )
					) : ''}
				</div>
			</div>
		</Layout>
	)
});

singleproduct.getInitialProps = async function( context ) {

	let { query: { slug } } = context;

	const id = slug ? slug.split( '-' ).pop() : context.query.id;

	const res = await client.query(({
		query: PRODUCTS,
		variables: { id }
	}));

	return {
		products: res.data.product
	}

};

export default singleproduct;
