import gql from "graphql-tag";

const PRODUCTS = gql`query  p_roduct($id: ID!){

    product(id: $id) {
        sku
        slug
        shortDescription
        productId
        name
        id
        averageRating
        description
        image {
          id
          sourceUrl
          slug
          sizes(size: LARGE)
          title
        }
        galleryImages {
          nodes {
            id
            sourceUrl
            slug
          }
        }
      }
}`;

export default PRODUCTS;
