import Layout from "../components/home/Layout";
import Category from "../components/home/Category";
import client from "../components/ApolloClient";
import CATEGORY_LIST from "../queries/categorieslist";

const index = (props) => {
	console.warn( props );
	const { category,npro } = props;
	return( 
		<Layout>
			<div className="product-container">
				{ category.length ? (
					category.map( cat => <Category key={ cat.id } cat={ cat }/> )
						) : '' }
			</div>
			<div className="col-xl-3 col-lg-3 col-md-3 col-sm-6 col-6">
            <div className="card text-center card-product">
                <div className="card-product__img">
                {npro.nodes.map(name => (
                    <Link  href={`/listproduct?slug=${ name.slug }-${ name.id }`}>
                        <a><img src={ name.image.sourceUrl} alt="Product image"/></a>
                    </Link>
                ))}
                </div>
                <div>
                {npro.nodes.map(nam => (
                    <h4 className="card-product__title"><a href="#">{ nam.name ? nam.name : '' }</a></h4>
                ))}
                </div>
            </div>
        </div>
		</Layout>
		)
	};
	index.getInitialProps = async () => {

		const result = await client.query( {
			query: CATEGORY_LIST,
		} );	
		return {
			npro:result.data.productCategories.nodes.products,
			category: result.data.productCategories.nodes
		}
	
	};
export default index;
