import Layout from "../components/home/Layout";
import Category from "../components/home/Category";
import client from "../components/ApolloClient";
import CATEGORY_LIST from "../queries/categorieslist";
import PRODUCTS_NEW  from "../queries/newpro";
import Newproducts from "../components/home/Newproducts";
const index = (props) => {
	console.warn( props );
	const { category,newproduct } = props;
	return( 
		<Layout>
			<div className="product-container">
				{ category.length ? (
					category.map( cat => <Category key={ cat.id } cat={ cat }/> )
						) : '' }
			</div>
			<div className="product-container">
				{ newproduct.length ? (
					newproduct.map( newp => <Newproducts key={ newp.id } newp={ newp }/> )
						) : '' }
			</div>
		</Layout>
		)
	};
	index.getInitialProps = async () => {

		const result = await client.query( {
			query: CATEGORY_LIST,
		} );
		const res = await client.query( {
			query: PRODUCTS_NEW,
		} );
	
		return {
			category: result.data.productCategories.nodes,
			newproduct:res.data.products.nodes
		}
	
	};
export default index;
