import Link from 'next/link';
const Relatedproducts = (propl) =>{
    const { lst } = propl;
    return(
        <div className="col-xl-3 col-lg-3 col-md-3 col-sm-6 col-6">
        <div className="card text-center card-product">
            <div className="card-product__img">
             {lst. products.nodes.map(name => (
                <Link  href={`singlepro/?slug=${ name.slug }-${ name.id }`,`singlepro/?slug=${ lst.slug }-${ lst.id }`}>
                    <a><img src={ name.image.sourceUrl} alt="Product image"/></a>
                </Link>
            ))}
                <div className="favorit-items">
                    <i className="ti ti-heart"></i>
                </div>
            </div>
            {lst. products.nodes.map(nam => (
            <div>
                <h4 className="card-product__title">
                    <Link  href={`singlepro/?slug=${ nam.slug }-${ nam.id }`,`/singlepro?slugr=${ lst.slug }-${ lst.id }`}>
                        <a href="#">{ nam.name ? nam.name : '' }</a>
                    </Link>
                </h4>
                <p className="card-product__price"><span className="cross"><del>{ nam.regularPrice ? nam.regularPrice : '' }
                </del></span>&nbsp;&nbsp;{ nam.salePrice ? nam.salePrice : '' }</p>
            </div>
            ))}
        </div>
    </div>
    );
}
export default Relatedproducts;
