import client from "../ApolloClient";
import PRODUCTS_NEW from "../../queries/newpro";
import Link from 'next/link';
import React from "react";
const Newproducts = (propn) => {
	const { category } = propn;
    return(
        <div className="col-xl-3 col-lg-3 col-md-3 col-sm-6 col-6">
            <div className="card text-center card-product">
                <div className="card-product__img">
                { category.length ? (
                        <Link  href={`/listproduct?slug=${ name.slug }-${ name.id }`}>
                            <a><img src={ name.image.sourceUrl} alt="Product image"/></a>
                        </Link>
                    ) : '' }
                </div>
                <div>
                    {newpro.map(nam => (
                    <h4 className="card-product__title"><a href="#">{ nam.name ? nam.name : '' }</a></h4>
                    ))} 
                </div>
            </div>
        </div>
    );
}
Newproducts.getInitialProps = async () => {

    const result = await client.query( {
        query: PRODUCTS_NEW,
    } );

    return {
        category: result.data.products.nodes
    }

};
export default Newproducts;
