import Layout from '../components/single/Layout';
import Singleproduct from '../components/single/Singleproduct';
import { withRouter } from 'next/router';
import client from "../components/ApolloClient";
import SINGLE_PRODUCTS from "../queries/sinproduct";
const singlepro = withRouter( props => {

	const { products } = props;

	return (
		<Layout>
			<div className="content-wrap">
				<div className="product-container row">
					{ undefined !== products && products.length ? (
						<div className="col-xl-3 col-lg-3 col-md-3 col-sm-6 col-6">
						<div className="card text-center card-product">
							<div className="card-product__img">
								<Link  href={`singlepro/?slug=${ products.slug }-${ products.id }`}>
									<a><img src={ products.image.sourceUrl} alt="Product image"/></a>
								</Link>
								<div className="favorit-items">
									<i className="ti ti-heart"></i>
								</div>
							</div>
							<div>
								<h4 className="card-product__title">
									<Link  href={`/singlepro?slug=${ products.slug }-${ products.id }`}>
										<a href="#">{ products.name ? products.name : '' }</a>
									</Link>
								</h4>
								<p className="card-product__price"><span className="cross"><del>{ products.regularPrice ? products.regularPrice : '' }
								</del></span>&nbsp;&nbsp;{ products.salePrice ? products.salePrice : '' }</p>
							</div>
						</div>
					</div>
					) : ' '}
				</div>
			</div>
		</Layout>
	)
});

singlepro.getInitialProps = async function( context ) {

	let { query: { slug } } = context;

	const id = slug ? slug.split( '-' ).pop() : context.query.id;

	const res = await client.query(({
		query: SINGLE_PRODUCTS,
		variables: { id }
	}));

	return {
		products: res.data.product
	}

};

export default singlepro;
