import { withRouter } from 'next/router';
import client from "../components/ApolloClient";
import PRODUCTS from "../queries/sinproduct";
import Sproduct from "../components/sproduct/Sproduct";
import Layout from "../components/sproduct/Layout";

const singleproduct = withRouter( props => {

	const { categoryName, products } = props;

	return (
		<Layout>
			<div className="content-wrap">
				<div className="product-container row">
					{ undefined !== products && products.length ? (
						products.map( lst => <Sproduct key={ lst.id } lst={ lst } /> )
					) : ''}
				</div>
			</div>
		</Layout>
	)
});

singleproduct.getInitialProps = async function( context ) {

	let { query: { slug } } = context;

	const id = slug ? slug.split( '-' ).pop() : context.query.id;

	const res = await client.query(({
		query: PRODUCTS_LIST,
		variables: { id }
	}));

	return {
		products: res.data.productCategory.products.nodes
	}

};

export default singleproduct;
