import Link from 'next/link';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Dropdown from 'react-bootstrap/Dropdown';
import Carousel from "react-elastic-carousel";
import Item from "./Item";
import Image from 'react-bootstrap/Image';
import React, { useEffect } from "react";
import useInputFile from "use-input-file";

const breakPoints = [
  { width: 1, itemsToShow: 1 },
  { width: 550, itemsToShow: 2 },
  { width: 768, itemsToShow: 4 },
  { width: 1200, itemsToShow: 5 },
];
const { ref, files } = useInputFile({
    options: {
      multiple: true
    }
  });

  useEffect(() => {
    if (files) {
      const [file] = files;
      console.log({
        name: file.name,
        size: file.size,
        type: file.type
      });
    }
  }, [files]);
const Singleproduct = (propl) =>{
    const { products } = propl;
    return(
        <div className="row">
                <div className="col-xl-2 col-lg-2"></div>
                <div className="col-xl-4 col-lg-4 col-md-6 col-sm-12 col-12">
                    <div className="row">
                        <Card style={{ width: '100%'}}>
                            <Card.Img variant="top" src={products.image.sourceUrl} />
                        </Card>
                    </div>
                    <div className="row">
                        <div className="App">
                            <Carousel breakPoints={breakPoints}>
                            {products. galleryImages.nodes.map(name => (
                                <Item style={{ height: '10rem', width: '12rem'}}>
                                    <Image src={name.sourceUrl} />
                                </Item>
                            ))}
                            </Carousel>
                        </div>
                    </div>
                </div>
            <div className="col-xl-4 col-lg-4 col-md-6 col-sm-12 col-12">
                <div className="product-details">
                    <div className="pd-title">
                        <span>Himalayan</span>
                            <h3>{products.name}</h3>
                        <a href="#" className="heart-icon"><i class="icon_heart_alt"></i></a>
                    </div>
                    <div className="pd-rating">
                        <i className="ti ti-star"></i>
                        <i className="ti ti-star"></i>
                        <i className="ti ti-star"></i>
                        <i className="ti ti-star"></i>
                        <i className="ti ti-star"></i>
                        <span>(5)</span>
                    </div>
                    <div className="pd-desc">
                        <p>Lorem ipsum dolor sit amet, consectetur ing elit, sed do eiusmod tempor sum dolor
                            sit amet, consectetur adipisicing elit, sed do mod tempor</p>
                            <p className="product__price"><span className="cross"><del>{ products.regularPrice ? products.regularPrice : '' }
                </del></span>&nbsp;&nbsp;{ products.salePrice ? products.salePrice : '' }</p>
                    </div>
                    <div className="sinbutton">
                        <div className="dropdown">
                        <Dropdown>
                            <Dropdown.Toggle variant="outline-danger" id="dropdown-basic">
                                Select Varient
                            </Dropdown.Toggle>

                            <Dropdown.Menu>
                                <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
                                <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
                                <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
                            </Dropdown.Menu>
                        </Dropdown>
                        <input ref={ref} type="file" />
                            {files && (
                            <p>
                                {JSON.stringify(
                                files.map((file) => file.name),
                                null,
                                2
                                )}
                            </p>
                            )}
                        </div>
                        <Button variant="outline-danger" size="lg" block>
                            ADD TO CART
                        </Button>
                    </div>
                    <div className="pd-share">
                        <div className="p-code">Sku : 00012</div>
                            <div className="pd-social">
                                <a href="#" className="socialicon"><i className="ti ti-facebook"></i></a>
                                <a href="#" className="socialicon"><i className="ti ti-twitter-alt"></i></a>
                                <a href="#" className="socialicon"><i className="ti ti-linkedin"></i></a>
                            </div>
                        </div>
                    </div>
            </div>
            <div className="col-xl-2 col-lg-2"></div>
    </div>
    );
}
export default Singleproduct;
