import gql from "graphql-tag";

/**
 * GraphQL categories and products query.
 */
const PRODUCTS_LIST = gql`query {

	products {
        nodes {
          ... on SimpleProduct {
            id
            name
            salePrice
            regularPrice
            productCategories {
              nodes {
                name
                id
              }
            }
          }
        }
      }					
}`;

export default PRODUCTS_LIST;
